package casting;

public class Downcasting {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
//larger data into smaller data
		//ex-1
		int i=20;
		// we have idea about are you coreectly pouring a/c to container
		byte b=(byte) i;
		System.out.println(b);
		
		//ex-2
		long l=798250l;
		int j= (int) l;
		System.out.println(j);
		
		//ex-3
		double d=123.45566;
		float f=  (float)d;
		System.out.println(f);
		
		
		
		
		
		
		
	}

}
